jQuery(function ($) {
	$(document).ready(function(){
		$("#customer_list").trigger('click');	

	});

	$(".customer-list li").on("click",function(e){
		var obj = e.target;
		$( ".customer-list li" ).each(function() {
			obj == this ? $(this).addClass("list-selected") : $(this).removeClass("list-selected");
		});
		var data = {
			action: "show_list",
			list_id: obj.id
		};
		$.ajax({
			type: "POST",
			url: ajaxurl,
			data: data,
			success: function(response){
				$("#main_content").html(response);
			},
			error: function(XMLHttpRequest, textStatus, errorThrown) {

			}
		});
	});

	$("#customer_select").on("change", function(e){
		if (e.target.value == 0) return;
		$(".customer-body").hide();
		if (e.target.value == 'Customer') {
			$(".customer-add").show();
			$(".group-add").hide();
		} else {
			$(".group-add").show();
			$(".customer-add").hide();
		}
	});

    $('.multi-switch').multiSwitch({
		functionOnChange: function ($element) {
        	console.log($element);
    	}
    });

	$(".customer-button").on("click", function(e){
		$(".customer-body").show();
		$(".customer-add").hide();
		$(".group-add").hide();
	});

	$(".customer-body").hide();
	$(".customer-add").show();
	$(".group-add").hide();

	$("#shipping_check").on("change", function(){
		if(this.checked) {
			$("#shipping_data").hide();
			$("#billing_title").text("Billing , Shipping Address");
		} else {
			$("#shipping_data").show();
			$("#billing_title").text("Billing Address");			
		}
	});

});